import { useParams } from "react-router-dom";

export default function CourseDetail() {
  const { id } = useParams();

  return (
    <div style={{ padding: "20px", textAlign: "center" }}>
      <h2>Course Details: {id.toUpperCase()}</h2>
      <p>
        Welcome to the <b>{id}</b> course.  
        Here you will find syllabus, trainers, pricing, and enrollment steps.
      </p>
    </div>
  );
}
