import { NavLink } from "react-router-dom";

export default function Navbar() {
  const linkStyle = ({ isActive }) => ({
    textDecoration: "none",
    padding: "12px 20px",
    fontWeight: "600",
    borderRadius: "8px",
    color: isActive ? "#fff" : "#333",
    background: isActive
      ? "linear-gradient(45deg, #ff4b2b, #ff416c)"
      : "transparent",
    boxShadow: isActive ? "0px 4px 12px rgba(0,0,0,0.2)" : "none",
    transition: "all 0.4s ease",
  });

  return (
    <nav
      style={{
        display: "flex",
        justifyContent: "center",
        gap: "20px",
        padding: "15px",
        background: "linear-gradient(90deg, #667eea, #764ba2)",
        borderBottom: "2px solid #ddd",
        position: "sticky",
        top: 0,
        zIndex: 1000,
      }}
    >
      <NavLink to="/" end style={linkStyle}>Home</NavLink>
      <NavLink to="/all" style={linkStyle}>All Courses</NavLink>
      <NavLink to="/fullstack" style={linkStyle}>Full Stack</NavLink>
      <NavLink to="/datascience" style={linkStyle}>Data Science</NavLink>
      <NavLink to="/cybersecurity" style={linkStyle}>Cyber Security</NavLink>
      <NavLink to="/career" style={linkStyle}>Career</NavLink>
    </nav>
  );
}
