import { NavLink } from "react-router-dom";

export default function Navbar() {
  const linkStyle = ({ isActive }) => ({
    textDecoration: "none",
    padding: "10px 15px",
    fontWeight: "500",
    color: isActive ? "green" : "black",
    borderBottom: isActive ? "3px solid green" : "3px solid transparent",
    transition: "all 0.3s ease",
  });

  return (
    <nav
      style={{
        display: "flex",
        justifyContent: "center",
        gap: "25px",
        borderBottom: "1px solid #ddd",
        marginBottom: "20px",
      }}
    >
      <NavLink to="/" end style={linkStyle}>ALL</NavLink>
      <NavLink to="/fullstack" style={linkStyle}>FULL STACK DEVELOPMENT</NavLink>
      <NavLink to="/datascience" style={linkStyle}>DATA SCIENCE</NavLink>
      <NavLink to="/cybersecurity" style={linkStyle}>CYBER SECURITY</NavLink>
      <NavLink to="/career" style={linkStyle}>CAREER</NavLink>
    </nav>
  );
}
